(function($) {
  $(document).ready(function() {
    $("#indMainSection .text-wrap").addClass('textOn');
    
    const sectionSelect = document.querySelectorAll(".section");
    for (var i = 0; i < sectionSelect.length; i++) {
      var sectionUse = sectionSelect[i].dataset.use;
      if (sectionUse == "off") {
        sectionSelect[i].remove();
      }
    }
    $('.main-product-01').eq(0).addClass('categoryActive');
    $('.cate-menu ul li').eq(0).addClass('categoryLiActive');
    $('.cate-menu ul li').click(function(event) {
      var categoryClickIndex = $(this).index();
      categoryActiveEq = categoryClickIndex;
      var categoryCorrectSlide = $('.main-product-01').eq(categoryClickIndex);
      var categoryOtherSlide = $('.main-product-01').not(categoryClickIndex);
      var categoryOtherLi = $('.cate-menu ul li').not(categoryClickIndex);
      var categoryCorrectLi = $('.cate-menu ul li').eq(categoryClickIndex);
      categoryOtherLi.removeClass('categoryLiActive');
      categoryCorrectLi.addClass('categoryLiActive');
      $('.main-product-01').removeClass('categoryActive');
      $('.main-product-01').eq(categoryClickIndex).addClass('categoryActive')
    });
    var categoryActiveEq = 0;
    var categoryLiIndex = $('.cate-menu ul li').length - 1;

    function Index03SlideChange() {
      var categoryCorrectSlide = $('.main-product-01').eq(categoryActiveEq);
      var categoryOtherSlide = $('.main-product-01').not(categoryActiveEq);
      var categoryOtherLi = $('.cate-menu ul li').not(categoryActiveEq);
      var categoryCorrectLi = $('.cate-menu ul li').eq(categoryActiveEq);
      categoryOtherLi.removeClass('categoryLiActive');
      categoryCorrectLi.addClass('categoryLiActive');
      $('.main-product-01').removeClass('categoryActive');
      $('.main-product-01').eq(categoryActiveEq).addClass('categoryActive')
    };

    const main_Event_Sw = new Swiper('#mainEventSw', {
      slidesPerView: 1.4,
      spaceBetween: 15,
      scrollbar: {
        el: '#mainSection_event .swiper-scrollbar',
        draggable: true,
      },
    });

    const main_Review_Sw = new Swiper('#mainSection_Review_Slide', {
      loop: true,
      autoplay: {
        delay: 5000,
      },
      autoHeight: true,
      slidesPerView: 1.5,
      spaceBetween: 15,
      centeredSlides: true,
      scrollbar: {
        el: '#mainSection_Review .swiper-scrollbar',
        draggable: true,
      },
    });


  });
})(jQuery);
