(function($) {
  var k = location.href;
  if (k.indexOf('board') > 0 && $('body,html').find('.bList').length !== 0) {
    var l = k.split("board")[1];
    var m = false;
    $(".bList").each(function() {
      var a = $(this).find('a');
      var b = a.attr("href");
      var c = b.split("board")[1];
      if (l == c) {
        $(".bList").removeClass('on');
        $(this).addClass('on');
        m = true;
        return false
      }
    });
    if (m == false) {
      var n = decodeURIComponent(k);
      var o = n.split("board")[1];
      var p = false;
      while (p == false) {
        if (o == "/공지사항/1/" || o == "/NOTICE/1/") {
          $(".bList").removeClass('on');
          $(".bList").eq(0).addClass('on');
          p = true
        } else if (o == "/product-01/list.html" || o == "/실시간-제품-후기/4/" || o == "/상품후기/4/") {
          $(".bList").removeClass('on');
          $(".bList").eq(1).addClass('on');
          p = true
        } else if (o == "/상품문의/6/" || o == "/Q&A/6/") {
          $(".bList").removeClass('on');
          $(".bList").eq(2).addClass('on');
          p = true
        } else if (o == "/newsevent/8/" || o == "/EVENT/8/") {
          $(".bList").removeClass('on');
          $(".bList").eq(3).addClass('on');
          p = true
        }
        break
      }
    }
  }
  if (k.indexOf('list-video.html') < 0) {
    $(".title").find('span').each(function() {
      if ($(this).text() == "비디오") {
        $(this).closest('.xans-record-').hide()
      }
    })
  }
  if (k.indexOf('sort_method') >= 0) {
    var q = window.location.search;
    var r = q.split("&");
    var s = r[r.length - 1];
    for (var i = 0; i < r.length; i++) {
      if (r[i].includes('sort_method=')) {
        var t = r[i].split("sort_method=");
        var u = t[1];
        break
      }
    }
    var v = $(".list-best-menu ul li").eq(1).attr('onclick');
    var w = v.slice(v.indexOf("?"), v.indexOf("#"));
    var x = w.split("&");
    var y = x[x.length - 1].split("sort_method=");
    var z = y[1];
    var A = new Array();
    var B;
    var C = $(".grid-item").length;
    if (u == z) {
      for (var i = 0; i < C; i++) {
        A[i] = (i + 1);
        B = "<p class='grid-item_best'>" + A[i] + "</p>";
        $('.grid-item').eq(i).prepend(B)
      }
    }
  }
  $(document).ready(function() {
    var g = $('.top-gnb-02').offset().top + ($('.top-gnb-02').height() / 2);
    var h = false;
    $(".tab_content").hide();
    $(".tab_content:first").show();
    $(".detail-tabs li").click(function() {
      $(".tab_content").hide();
      var a = $(this).attr("rel");
      $("#" + a).fadeIn();
      $(".detail-tabs li").removeClass("active");
      $(this).addClass("active");
      $(".tab_drawer_heading").removeClass("d_active");
      $(".tab_drawer_heading[rel^='" + a + "']").addClass("d_active")
    });
    $(".tab_drawer_heading").click(function() {
      $(".tab_content").hide();
      var a = $(this).attr("rel");
      $("#" + a).fadeIn();
      $(".tab_drawer_heading").removeClass("d_active");
      $(this).addClass("d_active");
      $(".detail-tabs li").removeClass("active");
      $(".detail-tabs li[rel^='" + a + "']").addClass("active")
    });
    $('.detail-tabs li').last().addClass("tab_last");
    var i = $(window).height() / 2;
    $(window).resize(function() {
      i = $(window).height() / 2
    });
    $(".quick-arrow-up").on('click', function(e) {
      $('html , body').stop().animate({
        scrollTop: "0"
      }, 500)
    });
    $(".quick-arrow-down").on('click', function(e) {
      var a = $(document).height();
      $('html , body').stop().animate({
        scrollTop: a
      }, 500)
    });
    var j = $('.quick-content');
    j.each(function() {
      var a = $(this).width();
      TweenMax.set($(this), {
        x: a
      })
    });
    $(".quick-icon").on('click', function(e) {
      var a = true;
      var b = $(".quick-icon").index(this);
      var c = j.eq(b);
      var d = c.width();
      j.removeClass('active');
      c.addClass('active');
      if (b == 0) {
        var f = $('.quick-content-00 .xans-record-').length;
        if (f > 0) {
          $('.quick-content-00').find('.message').hide()
        }
      }
      TweenMax.to('#quick-wrap', 0.5, {
        x: -d
      });
      $(document).on('click', function(e) {
        if ($(e.target).closest('#quick-wrap').length == 0 && a == true) {
          TweenMax.to('#quick-wrap', 0.5, {
            x: 0
          });
          a = false
        }
      })
    });

    function quickArrow() {
      var a = $(window).scrollTop();
      if (a > i) {
        $(".quick-arrow-wrap").removeClass('top');
        $(".quick-arrow-wrap").addClass('middle')
      } else {
        $(".quick-arrow-wrap").removeClass('middle');
        $(".quick-arrow-wrap").addClass('top')
      }
    };
    $(window).scroll(function() {
      quickArrow()
    })
  })
})(jQuery);
